import React, { useState, useEffect } from 'react';
import { Button, makeStyles } from '@material-ui/core';
import { useParams } from 'react-router-dom';
import { getUsers, editUser } from '../Service/api';

const initialValue = {
    name: '',
    username: '',
    email: '',
    phone: ''
}

const useStyles = makeStyles({
    container: {
        width: '50%',
        margin: '5% 0 0 25%',
        '& > *': {
            marginTop: 20
        }
    }
})

const EditUser = () => {
    const [user, setUser] = useState(initialValue);
    const { name, username, email, phone } = user;
    const { id } = useParams();
    const classes = useStyles();

    const loadUserDetails = async() => {
        const response = await getUsers(id);
        setUser(response.data);
    }

    const editUserDetails = async() => {
        const response = await editUser(id, user);
        response.push('/all');
    }

    const onValueChange = (e) => {
        console.log(e.target.value);
        setUser({...user, [e.target.name]: e.target.value})
    }

    return (
        <div className={classes.container}>
            <div>Edit Information</div>
            <div>
                <div htmlFor="my-div">Name</div>
                <div onChange={(e) => onValueChange(e)} name='name' value={name} id="my-div" />
            </div>
            <div>
                <div htmlFor="my-div">Username</div>
                <div onChange={(e) => onValueChange(e)} name='username' value={username} id="my-div" aria-describedby="my-helper-text" />
            </div>
            <div>
                <div htmlFor="my-div">Email</div>
                <div onChange={(e) => onValueChange(e)} name='email' value={email} id="my-div" aria-describedby="my-helper-text" />
            </div>
            <div>
                <div htmlFor="my-div">Phone</div>
                <div onChange={(e) => onValueChange(e)} name='phone' value={phone} id="my-div" aria-describedby="my-helper-text" />
            </div>
            <div>
                <Button variant="contained" color="primary" onClick={() => editUserDetails()}>Edit User</Button>
            </div>
        </div>
    )
}

export default EditUser;
