import react, { useState } from 'react';
import { Button, makeStyles } from '@material-ui/core';
import { addUser } from '../Service/api';
import { useHistory } from 'react-router-dom';

const initialValue = {
    name: '',
    username: '',
    email: '',
    phone: ''
}

const useStyles = makeStyles({
    container: {
        width: '50%',
        margin: '5% 0 0 25%',
        '& > *': {
            marginTop: 20
        }
    }
})

const AddUser = () => {
    const [user, setUser] = useState(initialValue);
    const { name, username, email, phone } = user;
    const classes = useStyles();
    let history = useHistory();

    const onValueChange = (e) => {
        console.log(e.target.value);
        setUser({...user, [e.target.name]: e.target.value})
    }

    const addUserDetails = async() => {
        await addUser(user);
        history.push('./all');
    }

    return (
        <div className={classes.container}>
            <div>"Add User"</div>
            <div>
                <div htmlFor="my-div">Name</div>
                <div onChange={(e) => onValueChange(e)} name='name' value={name} id="my-div" />
            </div>
            <div>
                <div htmlFor="my-div">Username</div>
                <div onChange={(e) => onValueChange(e)} name='username' value={username} id="my-div" />
            </div>
            <div>
                <div htmlFor="my-div">Email</div>
                <div onChange={(e) => onValueChange(e)} name='email' value={email} id="my-div"/>
            </div>
            <div>
                <div htmlFor="my-div">Phone</div>
                <div onChange={(e) => onValueChange(e)} name='phone' value={phone} id="my-div" />
            </div>
            <div>
                <Button variant="contained" color="primary" onClick={() => addUserDetails()}>Add User</Button>
            </div>
        </div>
    )
}

export default AddUser;
